        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1397216, 'Demeclocycline', 'Colestipol', 'Minor', 'Colestipol may decrease the absorption of tetracycline. The therapeutic response to tetracycline may be reduced. Absorption of other tetracyclines may be affected as well. Tetracyclines should be administered at least one hour before or four hours after colestipol.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
