    ) VALUES (1397530, 'Ioflupane I-123', 'Prochlorperazine', 'Moderate', 'Theoretically, dopamine agonists (e.g., antiparkinson agents, ergot derivatives) and antagonists (e.g., neuroleptics, antiemetics) may affect imaging results obtained using ioflupane I-123, which binds to the dopamine transporter.', NULL, 'Clinicians using ioflupane I-123 should be aware of possible diagnostic interference by dopamine agonists and antagonists.', NULL, 'Clinicians using ioflupane I-123 should be aware of possible diagnostic interference by dopamine agonists and antagonists.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
