INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123003, 'Maraviroc', 'Ketoconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of maraviroc, which is primarily metabolized by the isoenzyme. Patients with severe renal impairment or end-stage renal disease (CrCl <30 mL/min) given maraviroc in combination with potent CYP450 3A4 inhibitors may have an increased risk of postural hypotension due to increased maraviroc exposure.', NULL, 'Maraviroc should be administered at a dosage of 150 mg twice daily during coadministration with potent CYP450 3A4 inhibitors, including most protease inhibitors except tipranavir plus ritonavir.', NULL, 'Maraviroc should be administered at a dosage of 150 mg twice daily during coadministration with potent CYP450 3A4 inhibitors, including most protease inhibitors except tipranavir plus ritonavir. This dosage is recommended whether or not a concomitant CYP450 3A4 inducer (e.g., efavirenz, rifampin, carbamazepine, phenobarbital, phenytoin) is given. Patients with moderate hepatic impairment who receive 150 mg with a potent CYP450 3A4 inhibitor should be monitored closely for maraviroc-associated adverse events. Maraviroc is contraindicated for use in combination with potent CYP450 3A4 inhibitors in patients with severe renal impairment or end-stage renal disease (CrCl <30 mL/min).', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123152/', '[1] "Product Information. Selzentry (maraviroc)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[4] "Product Information. Selzentry (maraviroc)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123004, 'Medroxyprogesterone acetate', 'Ketoconazole', 'Moderate', 'Azole antifungal agents may increase the plasma concentrations of estrogens and progestins. The mechanism is decreased clearance of the hormones due to inhibition of CYP450 3A4 activity by azole antifungals.', NULL, 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', NULL, 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123153/', '[1] Lazar JD, Wilner KD "Drug interactions with fluconazole." Rev Infect Dis 12 Suppl 3 (1990): s327-33[2] Weisberg E "Interactions between oral contraceptives and antifungals antibacterials - Is contraceptive failure the result?." Clin Pharmacokinet 36 (1999): 309-13[3] Pillans PI, Sparrow MJ "Pregnancy associated with a combined oral contraceptive and itraconazole." N Z Med J 106 (1993): 436[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[5] Devenport MH, Crook D, Wynn V, Lees LJ "Metabolic effects of low-dose fluconazole in healthy female users and non-users of oral contraceptives." Br J Clin Pharmacol 27 (1989): 851-9[6] Sinofsky FE, Pasquale SA "The effect of fluconazole on circulating ethinyl estradiol levels in women taking oral contraceptives." Am J Obstet Gynecol 178 (1998)