INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123005, 'Mefloquine', 'Ketoconazole', 'Major', 'Coadministration with ketoconazole may significantly increase the plasma concentrations of mefloquine. The proposed mechanism is ketoconazole inhibition of mefloquine metabolism via hepatic CYP450 3A4. Because mefloquine prolongs the QT interval in a dose-dependent manner, high plasma levels of mefloquine may increase the risk of ventricular arrhythmias such as ventricular tachycardia, ventricular fibrillation, and torsade de pointes.', NULL, 'Ketoconazole should not be administered with mefloquine or within 15 weeks of the last dose of mefloquine due to increased risk of potentially fatal prolongation of the QTc interval.', NULL, 'Ketoconazole should not be administered with mefloquine or within 15 weeks of the last dose of mefloquine due to increased risk of potentially fatal prolongation of the QTc interval.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123154/', '[1] Ridtitid W, Wongnawa M, Mahatthanatrakul W, Raungsri N, Sunbhanich M "Ketoconazole increases plasma concentrations of antimalarial mefloquine in healthy human volunteers." J Clin Pharm Ther 30 (2005): 285-90[2] "Product Information. Lariam (mefloquine)." Roche Laboratories, Nutley, NJ.[3] Schmidt LE, Dalhoff K "Food-drug interactions." Drugs 62 (2002): 1481-502[4] "Product Information. Lariam (mefloquine)." Roche Laboratories, Nutley, NJ.', 1767365207);
