INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123040, 'Nizatidine', 'Ketoconazole', 'Moderate', 'H2 antagonists may reduce the bioavailability of ketoconazole by 75% to 80% resulting in decreased plasma ketoconazole concentrations and possible therapeutic failure. The mechanism is related to an increase in gastric pH and a decrease in absorption of the antifungal agent.', NULL, 'Some manufacturers of ketoconazole suggest administering ketoconazole with an acidic beverage (e.', NULL, 'Some manufacturers of ketoconazole suggest administering ketoconazole with an acidic beverage (e.g., non-diet cola, orange juice) if used concomitantly with H2 antagonists (US, GB, CA, BR). Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever H2 antagonists are added to or withdrawn from therapy with ketoconazole.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123189/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Ketoconazole (ketoconazole)." Mylan Pharmaceuticals Inc, Morgantown, WV.[3] Chin TWF, Loeb M, Fong IW "Effects of an acidic beverage (coca-cola) on absorption of ketoconazole." Antimicrob Agents Chemother 39 (1995): 1671-5[4] Adachi M, Hinatsu Y, et.al "Improved dissolution and absorption of ketoconazole in the presence of organic acids as pH-modifiers." Eur J Pharm Sci 76 (2015): 225-30[5] Carlson JA, Mann HJ, Canafax DM "Effect of pH on disintegration and dissolution of ketoconazole tablets." Am J Hosp Pharm 40 (1983): 1334-6[6] Piscitelli SC, Goss TF, Wilton JH, D''Andrea DT, Goldstein H, Schentag JJ "Effects of ranitidine and sucralfate on ketoconazole bioavailability." Antimicrob Agents Chemother 35 (1991): 1765-71[7] Cerner Multum, Inc "ANVISA Bulário Eletrônico. Available from: URL: http://www.anvisa.gov.br/datavisa/fila_bula/index.asp." O 0 (2015):[8] Blum RA, D''Andrea DT, Florentino BM, et al "Increased gastric pH and the bioavailability of fluconazole and ketoconazole." Ann Intern Med 114 (1991): 755-7[9] Van der Meer JW, Keuning JJ "The influence of gastric acidity on the bio-availability of ketoconazole." J Antimicrob Chemother 6 (1980): 552-4[10] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123041, 'Norfloxacin', 'Ketoconazole', 'Moderate', 'Certain quinolones, including levofloxacin, norfloxacin, and ofloxacin, may cause dose-related prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Although the risk of a serious interaction is probably low, caution is recommended if levofloxacin, norfloxacin, or ofloxacin is used in combination with other drugs that can prolong the QT interval.', NULL, 'Although the risk of a serious interaction is probably low, caution is recommended if levofloxacin, norfloxacin, or ofloxacin is used in combination with other drugs that can prolong the QT interval. Since the magnitude of QTc prolongation increases with increasing plasma concentrations of the quinolone, recommended dosages and intravenous infusion rates should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123190/', '[1] Iannini PB "Cardiotoxicity of macrolides, ketolides and fluoroquinolones that prolong the QTc interval." Expert Opin Drug Saf 1 (2002): 121-8[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Levaquin (levofloxacin)." Ortho Pharmaceutical Corporatio