INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123072, 'Pexidartinib', 'Ketoconazole', 'Major', 'Coadministration of pexidartinib with strong CYP450 3A4 inhibitors and/or uridine diphosphate glucuronosyltransferase (UGT) inhibitors may significantly increase the plasma concentrations and the incidence and severity of adverse effects of pexidartinib, including potentially fatal hepatotoxicity. The proposed mechanism is inhibition of CYP450 3A4 and/or UGT, the primary isoenzymes responsible for the metabolic clearance of pexidartinib.', NULL, 'The use of pexidartinib with strong CYP450 3A4 inhibitors and/or UGT inhibitors should generally be avoided.', NULL, 'The use of pexidartinib with strong CYP450 3A4 inhibitors and/or UGT inhibitors should generally be avoided. If concomitant use is required, the dose of pexidartinib should be reduced according to the manufacturer''s recommendations. If concomitant use of a strong CYP450 3A4 inhibitor or UGT inhibitor is discontinued, the dose of pexidartinib may be increased, after 3 plasma half-lives of the strong CYP450 3A4 inhibitor or UGT inhibitor, to the dose that was used prior to starting the strong CYP450 3A4 inhibitor or UGT inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123221/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123073, 'Phenobarbital', 'Ketoconazole', 'Moderate', 'The concomitant administration of potent CYP450 3A4 inducers may significantly reduce itraconazole and ketoconazole serum levels. The mechanism appears to be enhanced first-pass metabolism and hepatic enzyme metabolism (CYP450 3A4) of the antifungal agents. Antifungal therapy may fail.', NULL, 'Coadministration of potent CYP450 3A4 inducers with itraconazole or ketoconazole should be avoided.', NULL, 'Coadministration of potent CYP450 3A4 inducers with itraconazole or ketoconazole should be avoided. Some authorities recommend avoiding concomitant therapy with potent CYP450 3A4 inducers from 2 weeks before and during treatment with itraconazole. Fluconazole is eliminated unchanged by the kidneys and may be a more appropriate antifungal agent, depending on the patient''s condition.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123222/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Nizoral (ketoconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123074, 'Phenolphthalein', 'Ketoconazole', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123223/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Schaefer DC, Cheskin LJ "Constipation in the elderly." Am Fam Physician 58 (1998): 907-14[4] Muller-Lissner SA "Adverse effects of laxatives: fact and fiction." Pharmacology 47 (1993): 138-45[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] Chin RL "Laxative-induced hypokalemia." Ann Emerg Med 32 (1998): 517-8', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123075, 'Phenytoin', 'Ketoconazole', 'Moderate', 'Phenytoin and other hydantoins may reduce itraconazole serum levels by up to 90%. The mechanism appears to be enhanced first-pass metabolism and hepatic enzyme metabolism (CYP450 3A4) of itraconazole. Itraconazole therapy may fail. Ketoconazole is affected in a similar manner. In addition, itraconazole may increase the plasma concentrations of phenytoin.', NULL, 'Coadministration of phenytoin or other hydantoins with itraconazole or ketoconazole should be avoided from 2 weeks before and during treatment with itraconazole or ketoconazole.', NULL, 'Coadministration of phenytoin or other hydantoins with itraconazole or ketoconazole should be avoided from 2 weeks before and during treatment with itraconazole or ketoconazole. Some experts suggest that if concomitant use is unavoidable, a dosage increase for itraconazole or ketoconazole should be considered. In addition, monitoring of phenytoin concentrations is recommended and the dosage adjusted if needed. Fluconazole is eliminated unchanged by the kidneys and may be a more appropriate antifungal agent, depending on the patient''s condition.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123224/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Dilantin (phenytoin)." Parke-Davis, Morris Plains, NJ.[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Ozuna J, Friel P "Effect of enteral tube feeding on serum phenytoin levels." J Neurosurg Nurs 16 (1984): 289-91[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] "Product Information. Dilantin (phenytoin)." Parke-Davis, Morris Plains, NJ.[9] Doak KK, Haas CE, Dunnigan KJ, et al. "Bioavailability of phenytoin acid and phenytoin sodium with enteral feedings." Pharmacotherapy 18 (1998): 637-45[10] Haley CJ, Nelson J "Phenytoin-enteral feeding interaction." DICP 23 (1989): 796-8[11] Rodman DP, Stevenson TL, Ray TR "Phenytoin malabsorption after jejunostomy tube delivery." Pharmacotherapy 15 (1995): 801-5[12] Faraji B, Yu PP "Serum phenytoin levels of patients on gastrostomy tube feeding." J Neurosci Nurs 30 (1998): 55-9[13] Au Yeung SC, Ensom MH "Phenytoin and enteral feedings: does evidence support an interaction?" Ann Pharmacother 34 (2000): 896-905[14] Sandor P, Sellers EM, Dumbrell M, Khouw V "Effect of short- and long-term alcohol use on phenytoin kinetics in chronic alcoholics." Clin Pharmacol Ther 30 (1981): 390-7[15] Sellers EM, Holloway MR "Drug kinetics and alcohol ingestion." Clin Pharmacokinet 3 (1978): 440-52[16] Guidry JR, Eastwood TF, Curry SC "Phenytoin absorption in volunteers receiving selected enteral feedings." West J Med 150 (1989): 659-61[17] Holtz L, Milton J, Sturek JK "Compatibility of medications with enteral feedings." JPEN J Parenter Enteral Nutr 11 (1987): 183-6[18] Marvel ME, Bertino JS "Comparative effects of an elemental and a complex enteral feeding formulation on the absorption of phenytoin suspension." JPEN J Parenter Enteral Nutr 15 (1991): 316-8[19] Fleisher D, Sheth N, Kou JH "Phenytoin interaction with enteral feedings administered through nasogastric tubes." JPEN J Parenter Enteral Nutr 14 (1990): 513-6[20] Krueger KA, Garnett WR, Comstock TJ, Fitzsimmons WE, Karnes HT, Pellock JM "Effect of two administration schedules of an enteral nutrient formula on phenytoin bioavailability." Epilepsia 28 (1987): 706-12', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123076, 'Pimavanserin', 'Ketoconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of pimavanserin, which is primarily metabolized by the isoenzyme. High plasma levels of pimavanserin may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Pimavanserin dosage should be reduced to 10 mg once a day when used with potent CYP450 3A4 inhibitors.', NULL, 'Pimavanserin dosage should be reduced to 10 mg once a day when used with potent CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123225/', '[1] "Product Information. Nuplazid (pimavanserin)." Accelis Pharma, East Windsor, NJ.[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123077, 'Pimecrolimus (topical)', 'Ketoconazole', 'Minor', 'Theoretically, coadministration with CYP450 3A inhibitors may increase the plasma concentrations of pimecrolimus, which has been shown in vitro to undergo metabolism by this subfamily of hepatic microsomal enzymes. Although clinically significant drug interactions are not expected due to minimal systemic absorption of topically administered pimecrolimus, the possibility cannot be ruled out.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123226/', '[1] "Product Information. Elidel (pimecrolimus topical)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123078, 'Pimozide', 'Ketoconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of pimozide, which is partially metabolized by the isoenzyme. The use of pimozide has been associated with dose-related prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of pimozide, concomitant use with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors is considered contraindicated.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of pimozide, concomitant use with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, telithromycin, conivaptan, idelalisib, nefazodone, cobicistat, delavirdine, and most protease inhibitors is considered contraindicated. Some authorities consider concomitant administration of lurasidone and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole. With respect to less potent CYP450 3A4 inhibitors, the manufacturers recommend that they also not be used with pimozide.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scb