INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123164, 'Mitotane', 'Macimorelin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of macimorelin, which is primarily metabolized by the isoenzyme.', NULL, 'The prescribing information for macimorelin recommends discontinuing potent CYP450 3A4 inducers (e.', NULL, 'The prescribing information for macimorelin recommends discontinuing potent CYP450 3A4 inducers (e.g., carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, rifamycins, St. John''s wort) as well as some moderate ones (e.g., bosentan, efavirenz, etravirine, modafinil) prior to macimorelin administration. A sufficient washout period following discontinuation of the inducers is also advised before using macimorelin. No recommendations are available for other, less potent CYP450 3A4 inducers; however, it may be appropriate to follow the same precaution.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123313/', '[1] "Product Information. Macrilen (macimorelin)." Aeterna Zentaris, Charleston, SC.[2] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[3] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[4] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[5] "Product Information. Glucotrol (glipizide)." Pfizer US Pharmaceuticals, New York, NY.[6] "Position Statement: evidence-based nutrition principles and recommendations for the treatment and prevention of