INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123208, 'Pomalidomide', 'Mitotane', 'Moderate', 'Coadministration with potent inducers of CYP450 1A2, CYP450 3A4, and/or P-glycoprotein may decrease the plasma concentrations of pomalidomide, which has been shown to be primarily metabolized by these isoenzymes and also a substrate of the efflux transporter.', NULL, 'Concomitant use of pomalidomide with potent inducers of CYP450 isoenzymes and/or P-glycoprotein should generally be avoided.', NULL, 'Concomitant use of pomalidomide with potent inducers of CYP450 isoenzymes and/or P-glycoprotein should generally be avoided.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123357/', '[1] "Product Information. Pomalyst (pomalidomide)." QLT Phototherapeutics Inc, Vancouver, AZ.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[3] "Product Information. Pomalyst (pomalidomide)." QLT Phototherapeutics Inc, Vancouver, AZ.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123209, 'Ponatinib', 'Mitotane', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of ponatinib, which is a substrate of the isoenzyme.', NULL, 'Coadministration of ponatinib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', NULL, 'Coadministration of ponatinib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided unless the benefit outweighs the potential risk of ponatinib underexposure. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. If concomitant use is required, close monitoring for signs of reduced therapeutic efficacy is advised. Other known inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, etravirine, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with ponatinib is unknown.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123358/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[4] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123210, 'Pralsetinib', 'Mitotane', 'Major', 'Coadministration with potent inducers of CYP450 3A may significantly decrease the plasma concentrations of pralsetinib which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of pralsetinib with potent CYP450 3A inducers should generally be avoided.', NULL, 'Concomitant use of pralsetinib with potent CYP450 3A inducers should generally be avoided. If coadministration with potent CYP450 3A inducers is necessary, the initial dose of pralsetinib should be doubled starting on Day 7 of coadministration with the strong CYP450 3A4 inducer. Once the potent CYP450 3A inducer has been discontinued for at least 14 days, the pralsetinib dose taken prior to initiating the potent CYP450 3A4 inducer may be resumed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123359/', '[1] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation, Cambridge, MA.[2] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation, Cambridge, MA.[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123211, 'Praziquantel', 'Mitotane', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of praziquantel, which is a substrate of the isoenzyme.', NULL, 'Concomitant use of praziquantel with potent CYP450 3A4 inducers is considered contraindicated because therapeutically effective blood levels of praziquantel may not be achieved.', NULL, 'Concomitant use of praziquantel with potent CYP450 3A4 inducers is considered contraindicated because therapeutically effective blood levels of praziquantel may not be achieved.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123360/', '[1] Ridtitid W, Wongnawa M, Mahatthanatrakul W, Punyo J, Sunbhanich M "Rifampin markedly decreases plasma concentrations of praziquantel in healthy volunteers." Clin Pharmacol Ther 72 (2002): 505-13[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Biltricide (praziquantel)." Bayer, West Haven, CT.[4] Bittencourt PR, Gracia CM, Martins R, et al "Phenytoin and carbamazepine decrease oral bioavailability of praziquantel." Neurology 42 (1992): 492-6[5] Castro N, Medina R, Sotelo J, Jung H "Bioavailability of praziquantel increases with concomitant administration of food." Antimicrob Agents Chemother 44 (2000): 2903-4[6] Castro N, Jung H, Medina R, Gonzalez-Esquivel D, Lopez M, Sotelo J "Interaction between grapefruit juice and praziquantel in humans." Antimicrob Agents Chemother 46 (2002): 1614-6[7] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123212, 'Pretomanid', 'Mitotane', 'Major', 'Coadministration of pretomanid with strong or moderate CYP450 3A4 inducers may decrease the plasma concentrations and antimicrobial effects of pretomanid, which has been shown to be a substrate of the isoenzyme.', NULL, 'The use of pretomanid with strong or moderate CYP450 3A4 inducers should be avoided.', NULL, 'The use of pretomanid with strong or moderate CYP450 3A4 inducers should be avoided. Dosage adjustments as well as clinical and laboratory monitoring of pretomanid should be considered whenever a strong or moderate CYP450 3A4 inducer is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123361/', '[1] "Product Information. Pretomanid (pretomanid)." The Global Alliance for TB Drug Development, New York, NY.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[3] "Product Information. Pretomanid (pretomanid)." The Global Alliance for TB Drug Development, New York, NY.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123213, 'Primidone', 'Mitotane', 'Minor', 'Mitotane could theoretically decrease the plasma levels and clinical effectiveness of barbiturates by inducing their hepatic metabolism. Until more data are available, be cognizant of the possibility of decreased clinical effectiveness from barbiturates when mitotane is added to a patient''s regimen, as well as the risk of barbiturate toxicity upon mitotane withdrawal.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123362/', '[1] "Product Information. Lysodren (mitotane)." Bristol-Myers Squibb, Princeton, NJ.', 1767365207);
