        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1397986, 'Conivaptan', 'Methylergometrine', 'Major', 'Coadministration with conivaptan may significantly increase the plasma concentrations of ergot derivatives. The mechanism is conivaptan inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of ergotamine and related drugs.', NULL, 'Given the potential for ergot toxicity characterized by peripheral vasospasm, ischemia, thrombosis, tachycardia and hypertension, concomitant use of ergot derivatives with conivaptan should be avoided if possible.', NULL, 'Given the potential for ergot toxicity characterized by peripheral vasospasm, ischemia, thrombosis, tachycardia and hypertension, concomitant use of ergot derivatives with conivaptan should be avoided if possible. Patients currently receiving ergot derivatives should consider an interruption during administration of conivaptan, and allow an appropriate amount of time following completion of conivaptan therapy before resuming these medications.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
