        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1398068, 'Conivaptan', 'Silodosin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of silodosin, which is primarily metabolized by the isoenzyme. Severe hypotension and priapism may occur.', NULL, 'Concomitant use of silodosin with potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, 'Concomitant use of silodosin with potent CYP450 3A4 inhibitors is considered contraindicated. Some authorities recommend avoiding concomitant use of silodosin during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
