    ) VALUES (1398081, 'Conivaptan', 'Suvorexant', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of suvorexant, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of suvorexant with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of suvorexant with potent CYP450 3A4 inhibitors should generally be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
