INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123535, 'Esomeprazole', 'Modafinil', 'Moderate', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 2C19 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 2C19 activity by modafinil and armodafinil.', NULL, 'Caution is advised if modafinil or armodafinil must be used concurrently with medications that undergo metabolism by CYP450 2C19, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if modafinil or armodafinil must be used concurrently with medications that undergo metabolism by CYP450 2C19, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever modafinil or armodafinil is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123684/', '[1] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[2] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.[3] Dequardo JR "Modafinil-associated clozapine toxicity." Am J Psychiatry 159 (2002): 1243-1244[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Robertson P, Decory HH, Madan A, Parkinson A "In vitro inhibition and induction of human hepatic cytochrome P450 enzymes by modafinil." Drug Metab Dispos 28 (2000): 664-71[6] Olanoff LS, Walle T, Cowart TD, et al "Food effects on propranolol systemic and oral clearance: support for a blood flow hypothesis." Clin Pharmacol Th