INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123752, 'Amoxapine', 'Moexipril', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123901/', '[1] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991): 139[2] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990): 385-6[3] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991): 390-1[4] Pacher P, Kecskemeti V "Cardiovascular side effects of new antidepressants and antipsychotics: new drugs, old concerns?" Curr Pharm Des 10 (2004): 2463-75[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Andrews C, Pinner G "Postural hypotension induced by paroxetine." BMJ 316 (1998): 595[7] Rodriguez de la Torre B, Dreher J, Malevany I, et al. "Serum levels and cardiovascular effects of tricyclic antidepressants and selective serotonin reuptake inhibitors in depressed patients." Ther Drug Monit 23 (2001): 435-40[8] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[9] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[10] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[11] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123753, 'Aurothioglucose', 'Moexipril', 'Moderate', 'Concomitant ACE inhibitor therapy may potentiate the risk of nitritoid (vasomotor) reactions associated with injectable gold therapy. The mechanism of interaction is unknown, although investigators suggest that ACE inhibitors may unmask drug hypersensitivity reactions by preventing the breakdown of bradykinins.', NULL, 'Patients on injectable gold therapy in combination with an ACE inhibitor should be monitored for symptoms of nitritoid reactions such as facial flushing, nausea, vomiting, and hypotension following an injection.', NULL, 'Patients on injectable gold therapy in combination with an ACE inhibitor should be monitored for symptoms of nitritoid reactions such as facial flushing, nausea, vomiting, and hypotension following an injection. Elderly patients in particular should be observed following treatment. Oxygen, intravenous fluids, epinephrine, steroids, and other supportive therapy may be necessary for treatment.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123902/', '[1] "Product Information. Prinivil (lisinopril)." Merck & Co, Inc, West Point, PA.[2] Nixon J, Pande I "Gold, nitritoid reactions and angiotensin-converting enzyme inhibitors." Rheumatology (Oxford) 45 (2006): 118-9[3] Ray K, Dorman S, Watson R "Severe hyperkalaemia due to the concomitant use of salt substitutes and ACE inhibitors in hypertension: a potentially life threatening interaction." J Hum Hypertens 13 (1999): 717-20[4] "Product Information. Vasotec (enalapril)." Merck & Co, Inc, West Point, PA.[5] Good CB, McDermott L "Diet and serum potassium in patients on ACE inhibitors." JAMA 274 (1995): 538', 1767365207);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123754, 'Bedaquiline', 'Moexipril', 'Moderate', 'Coadministration of bedaquiline with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; other antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; ketolide and macrolide antibiotics; interferons; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), especially in patients with diminished hepatic reserve. Patients treated with bedaquiline should have serum ALT, AST, alkaline phosphatase, and bilirubin monitored at baseline and monthly during treatment, or as often as needed. An increase of serum aminotransferases to greater than 3 times ULN should be followed by repeat testing within 48 hours. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Discontinue bedaquiline if aminotransferase elevations are accompanied by total bilirubin elevation greater than 2 times ULN, aminotransferase elevations are greater than 8 times ULN, or aminotransferase elevations persist beyond 2 weeks.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123903/', '[1] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[3] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767365207);
