INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (123899, 'Sodium ferric gluconate complex', 'Moexipril', 'Moderate', 'Limited data suggest that ACE inhibitors may increase the risk and/or severity of systemic adverse effects associated with parenteral administration of iron. The exact mechanism of interaction is unclear. Certain systemic reactions stemming from intravenous iron therapy are thought to be mediated by inflammatory substances such as bradykinin in response to iron-catalyzed generation of toxic free radicals. Since ACE inhibitors decrease the breakdown of kinins, it is conceivable that they may potentiate these reactions.', NULL, 'No specific intervention is necessary to avert a potential interaction.', NULL, 'No specific intervention is necessary to avert a potential interaction. However, patients should be closely monitored during and after parenteral iron treatment.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124049/', '[1] "Product Information. Ferrlecit (sodium ferric gluconate complex)." sanofi-aventis, Bridgewater, NJ.[2] Michael B, Coyne DW, Folkert VW, Dahl NV, Warnock DG "Sodium ferric gluconate complex in haemodialysis patients: a prospective evaluation of long-term safety." Nephrol Dial Transplant 19 (2004): 1576-80[3] "Product Information. INFeD (iron dextran)." Schein Pharmaceuticals Inc, Florham Park, NJ.[4] Michael B, Coyne DW, Fishbane S, et al. "Sodium ferric gluconate complex in hemodialysis patients: Adverse reactions compared to placebo and iron dextran." Kidney Int 61 (2002): 1830-1839[5] Rolla G, Bucca C, Brussino L "Systemic reactions to intravenous iron therapy in patients receiving angiotensin c