        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1398101, 'Conivaptan', 'Tolvaptan', 'Major', 'Coadministration with conivaptan may significantly increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. Conivaptan is a mechanism-based inhibitor of CYP450 3A4.', NULL, 'Concomitant use of conivaptan with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index should generally be avoided.', NULL, 'Concomitant use of conivaptan with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index should generally be avoided. Subsequent treatment with CYP450 3A4 substrates may be initiated no sooner than 1 week after completion of conivaptan treatment.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
