INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248337, 'Diclofenac', 'Somapacitan', 'Moderate', 'Coadministration with human growth hormone may decrease the plasma concentrations of drugs that are metabolized by CYP450 3A4.  Data from an interaction study performed in growth hormone deficient adults suggest that growth hormone (somatropin) administration can increase the clearance of compounds known to be metabolized by cytochrome P450 isoenzymes, especially CYP450 3A4.', NULL, 'Caution is advised when human growth hormone is used concurrently with drugs that are known CYP450 3A4 substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when human growth hormone is used concurrently with drugs that are known CYP450 3A4 substrates, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever human growth hormone is added to or withdrawn from therapy.', 'Metabolism', 'Indomethacin, Fluocinolone acetonide, Fluorometholone, Salicylic acid, Ketorolac, Indomethacin, Fluocinolone acetonide, Piroxicam, Flurbiprofen, Fluorometholone, Bromfenac, More', 'Corticotropin, Mecasermin, Sermorelin, Tesamorelin, Thyrotropin alfa, Tetracosactide, Mecasermin rinfabate, Pegvisomant', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293551/', '[1] "Product Information. Humatrope (somatropin)." Lilly, Eli and Company  (2003):[2] "Product Information. Zorbtive (somatropin)." Serono Laboratories Inc  (2004):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
