INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248611, 'Abciximab', 'Phenyl salicylate', 'Moderate', 'Although aspirin is routinely given with glycoprotein IIb/IIIa inhibitors, be cognizant of the increased risk of bleeding associated with this combination due to the additive inhibitory effects of salicylates on platelet function.  Arterial puncture sites may be most problematic.', NULL, 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.', NULL, 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.  Patients should be closely monitored for signs of bleeding, especially at arterial puncture sites.  Therapy should be discontinued immediately if serious or uncontrollable bleeding or thrombocytopenia occurs.', 'Synergism', 'Dipyridamole, Caplacizumab, Cilostazol, Defibrotide, Antithrombin III human, Protein C, Acenocoumarol, Selexipag, Vorapaxar, Cangrelor, Ticlopidine', 'Darifenacin, Vardenafil, Tadalafil, Ammonium chloride, Apomorphine, Yohimbine, Papaverine, Oxybutynin, Dimethyl sulfoxide, Calcium chloride, Trospium, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/293867/', '[1] "Product Information. ReoPro (abciximab)." Lilly, Eli and Company  (2001):[2] "Product Information. Integrilin (eptifibatide)." Schering Corporation  (2001):[3] Klinkhardt U, Kirchmaier CM, Westrup D, Graff J, Mahnel R, Breddin HK, Harder S "Ex vivo-in vitro interaction between aspirin, clopidogrel, and the glycoprotein IIb/IIIa inhibitors abciximab and SR121566A." Clin Pharmacol Ther 67 (2000):  305-13[4] Hirsch J, Dalen J, Guyatt G, American College of Chest Physicians "The sixth (2000) ACCP guidelines for antithrombotic therapy for prevention and treatment of thrombosis. American College of Physicians." Chest 119(1 Suppl) (2001):  1S-2S', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248612, 'Acetohexamide', 'Phenyl salicylate', 'Moderate', 'The hypoglycemic effect of insulin secretagogues (e.g., sulfonylureas, meglitinides) may be potentiated by certain drugs, including ACE inhibitors, 4-aminoquinolines, amylin analogs, anabolic steroids, fibrates, monoamine oxidase inhibitors (MAOIs, including linezolid), nonsteroidal anti-inflammatory drugs (NSAIDs), salicylates, selective serotonin reuptake inhibitors (SSRIs), sulfonamides, disopyramide, propoxyphene, quinine, quinidine, and ginseng.  These drugs may increase the risk of hypoglycemia by enhancing insulin sensitivity (ACE inhibitors, fibrates, ginseng); stimulating insulin secretion (salicylates, NSAIDs, disopyramide, quinine, quinidine, MAOIs, ginseng); decreasing insulin clearance and resistance (4-aminoquinolines); increasing peripheral glucose utilization (SSRIs, insulin-like growth factor); inhibiting gluconeogenesis (SSRIs, MAOIs, insulin-like growth factor); slowing the rate of gastric emptying (amylin analogs); and/or suppressing postprandial glucagon secretion (amylin analogs).  Or, they may increase plasma concentration of insulin secretagogues by displacing them from plasma protein binding sites and/or inhibiting their metabolism (fibrates, NSAIDs, salicylates, sulfonamides).', NULL, 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impairment.', NULL, 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impai