INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248698, 'Heparin', 'Phenyl salicylate', 'Moderate', 'Theoretically, salicylates may potentiate the effects of anticoagulants and increase the risk of bleeding.', NULL, 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants.', NULL, 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants.  Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary.  The same precaution should be observed with the use of salicylate-related agents such as salicylamide because of their structural and pharmacological similarities.   Ambulatory patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.  Patients should also be counseled to avoid any other over-the-counter salicylate products.', 'Synergism', 'Dipyridamole, Caplacizumab, Cilostazol, Defibrotide, Antithrombin III human, Protein C, Acenocoumarol, Selexipag, Vorapaxar, Cangrelor, Ticlopidine, More', 'Apomorphine, Fesoterodine, Phenazopyridine, Acetohydroxamic acid, Trospium, Mirabegron, Yohimbine, Flavoxate, Darifenacin, Avanafil, Papaverine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/ser