INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248770, 'Reteplase', 'Phenyl salicylate', 'Moderate', 'Drugs that inhibit platelet function may increase the risk of bleeding when administered prior to, during, or after thrombolytic therapy.', NULL, 'Careful monitoring for signs of bleeding, particularly at arterial puncture wounds, is recommended when thrombolytics are used concurrently or sequentially with antiplatelet agents other than aspirin.', NULL, 'Careful monitoring for signs of bleeding, particularly at arterial puncture wounds, is recommended when thrombolytics are used concurrently or sequentially with antiplatelet agents other than aspirin.  Some authorities recommend avoiding the initiation of platelet aggregation inhibitors within the first 24 hours following thrombolysis treatment.', 'Synergism', 'Dipyridamole, Caplacizumab, Cilostazol, Defibrotide, Antithrombin III human, Protein C, Acenocoumarol, Selexipag, Vorapaxar, Cangrelor, Ticlopidine', 'Darifenacin, Vardenafil, Tadalafil, Ammonium chloride, Apomorphine, Yohimbine, Papaverine, Oxybutynin, Dimethyl sulfoxide, Calcium chloride, Trospium, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294037/', '[1] "Product Information. Ticlid (ticlopidine)." Syntex Laboratories Inc  (2001):[2] "Product Information. Abbokinase (urokinase)." Abbott Pharmaceutical  (2001):[3] "Product Information. Activase (alteplase)." Genentech  (2001):[4] "Product Information. Streptase (streptokinase)." Astra-Zeneca Pharmaceuticals  (2001):[5] "Product Information. Retavase (reteplase)." Boehringer Mannheim  (2001):[6] "Product Information. TNKase (tenecteplase)." Genentech  (2001):[7] Harder S, Klinkhardt U "Thrombolytics: drug interactions of clinical significance." Drug Saf 23 (2000):  391-9[8] Hirsch J, Dalen J, Guyatt G, American College of Chest Physicians "The sixth (2000) ACCP guidelines for antithrombotic therapy for prevention and treatment of thrombosis. American College of Physicians." Chest 119(1 Suppl) (2001):  1S-2S', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248771, 'Risedronic acid', 'Phenyl salicylate', 'Moderate', 'Theoretical concerns exist regarding the potential for increased risk and severity of gastrointestinal toxicity during coadministration of oral bisphosphonates and nonsteroidal anti-inflammatory drugs (NSAIDs) due to additive or synergistic irritant effects on the gastrointestinal mucosa.  Because NSAIDs reduce the rate of ulcer healing in the stomach and duodenum, it is also possible that NSAIDs may delay healing and exaggerate the mucosal injury caused by oral bisphosphonates.', NULL, 'Caution is advised if bisphosphonates are prescribed in combination with NSAIDs.', NULL, 'Caution is advised if bisphosphonates are prescribed in combination with NSAIDs.  Patients receiving oral bisphosphonates should be closely monitored for the development of gastrointestinal toxicity and advised to immediately report potential signs and symptoms such as severe abdominal pain, nausea, vomiting, diarrhea, loss of appetite, dizziness, lightheadedness, and/or black, tarry stools.  Patients receiving intravenous formulations of bisphosphonates should have renal function and serum electrolytes closely monitored.  Serum creatinine should be assessed prior to each treatment, and treatment should be withheld in the presence of renal deterioration.  In patients treated for bone metastases, treatment should not be resumed until renal function returns to baseline.', 'Synergism', 'Vosoritide, Denosumab, Cholecalciferol, Burosumab, Romosozumab', 'Apomorphine, Fesoterodine, Pentosan polysulfate, Phenazopyridine, Alprostadil, Trospium, Mirabegron, Acetohydroxamic acid, Yohimbine, Flavoxate, Avanafil, More', 'Moderate', NULL, 'DD