INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248807, 'Valsartan', 'Phenyl salicylate', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of angiotensin II receptor antagonists.  The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension.  In addition, NSAIDs can cause fluid retention, which also affects blood pressure.', NULL, 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.  Renal function should also be evaluated periodically during prolonged coadministration.  The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', 'Hydrochlorothiazide, Sacubitril, Nebivolol, Atorvastatin, Simvastatin, Lovastatin, Rosuvastatin, Pravastatin, Ezetimibe, Bempedoic acid, Indapamide, Fenofibrate', 'Fesoterodine, Pentosan polysulfate, Phenazopyridine, Acetohydroxamic acid, Trospium, Mirabegron, Yohimbine, Flavoxate, Papaverine, Magnesium hydroxide, Ammonium chloride, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294077/', '[1] Radack KL, Deck CC, Bloomfield SS "Ibupro