INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248817, 'Bupropion', 'Nitroprusside', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation.  Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.  Some authorities recommend avoiding alcohol in patients receiving vasodilating antihypertensive drugs.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Dexfenfluramine, Naltrexone, Ephedrine, Fenfluramine, Phentermine, Orlistat, Lorcaserin, Sibutramine, Diethylpropion, Mazindol, Citalopram, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294089/', '[1] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991):  390-1[2] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991):  139[3] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990):  385-6[4] Rodriguez de la Torre B, Dreher J, Malevany I, et al. "Serum levels and cardiovascular effects of tricyclic antidepressants and selective serotonin reuptake inhibitors in depressed patients." Ther Drug Monit 23 (2001):  435-40[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Pacher P, Kecskemeti V "Cardiovascular side effects of new antidepressants and antipsychotics: new drugs, old concerns?" Curr Pharm Des 10 (2004):  2463-75[7] Andrews C, Pinner G "Postural hypotension induced by paroxetine." BMJ 316 (1998):  595', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (248818, 'Bupropion', 'Telotristat ethyl', 'Moderate', 'Coadministration with inducers of CYP450 2B6 may decrease the plasma concentrations of bupropion.  In vitro findings suggest that CYP450 2B6 is the principal isoenzyme involved in the formation of hydroxybupropion, one of three active metabolites of bupropion.', NULL, 'Pharmacologic response to bupropion should be monitored more closely whenever a CYP450 2B6 inducer is added to or withdrawn from therapy, and the bupropion dosage adjusted as necessary.', NULL, 'Pharmacologic response to bupropion should be monitored more closely whenever a CYP450 2B6 inducer is added to or withdrawn from therapy, and the bupropion dosage adjusted as necessary.  Concomitant use of potent and moderate CYP450 2B6 inducers such as carbamazepine, efavirenz, rifampin, ritonavir, and lopinavir-ritonavir should be avoided with fixed-dose combination products such as bupropion-naltrexone or bupropion-dextromethorphan.  For other bupropion products, increases in bupropion dosage should be guided by clinical response; however, the maximum recommended dosage should not be exceeded.', 'Metabolism', 'Dexfenfluramine, Naltrexone, Ephedrine, Fenfluramine, Phentermine, Orlistat, Lorcaserin, Sibutramine, Diethylpropion, Mazindol, Maprotiline, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/294090/', '[1] James WA, Lippmann S "Bupropion: overview and prescribing guidelines in depression." South Med J 84 (1991):  222-4[2] "Product Information. Wellbutrin (bupropion)." Glaxo Wellcome  (2001):[3] Ketter TA, Jenkins JB, Schroeder DH, Pazzaglia PJ, Marangell LB, George MS, Callahan AM,