INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1398450, 'Corticotropin', 'Warfarin', 'Moderate', 'Corticosteroids and adrenocorticotropic agents may alter the pharmacologic effects of oral anticoagulants. Both increased anticoagulant dosage requirements as well as bleeding and increased anticoagulant sensitivity have been reported during concomitant corticosteroid therapy. Proposed mechanisms include blood hypercoagulability or diminished vascular integrity induced by corticosteroids.', 'DDInter', 1767317475, 'Close monitoring for clinical and laboratory evidence of altered anticoagulant response is recommended during concurrent treatment with corticosteroids or adrenocorticotropic agents. Patients should be advised to promptly report any signs and symptoms of bleeding or blood clots.', 'Others', 'Close monitoring for clinical and laboratory evidence of altered anticoagulant response is recommended during concurrent treatment with corticosteroids or adrenocorticotropic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
