INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1398531, 'Mepyramine', 'Levacetylmethadol', 'Major', 'The central nervous system and respiratory depressant effects of levomethadyl acetate may be potentiated by concomitant use of other agents with CNS depressant effects. An increased risk of serious adverse reactions such as respiratory depression, hypotension, profound sedation, syncope, coma, and even death should be considered.', 'DDInter', 1767317475, 'Caution and dosage adjustments are advisable if CNS depressants are prescribed to patients on levomethadyl acetate maintenance. Alcohol use should be avoided, and patients should be advised to avoid driving, operating machinery, or engaging in potentially hazardous activities requiring mental alertness and motor coordination until they know how the medications affect them. Patients who are known to abuse sedatives, tranquilizers, antidepressants, benzodiazepines, and alcohol should be warned of the risk of serious and potentially fatal overdose if these substances are taken concurrently.', 'Synergism', 'Caution and dosage adjustments are advisable if CNS depressants are prescribed to patients on levomethadyl acetate maintenance.', NULL, NULL, 'Major', 'pharmacodynamic');
