INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (124532, 'Iohexol', 'Moxifloxacin', 'Major', 'Intrathecal administration of iodinated contrast media may induce seizures. Although clinical data are generally lacking, there may be a theoretical risk of increased seizure potential when used with selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), and/or any substance that can reduce the seizure threshold (e.g., carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, theophylline). These agents are often individually epileptogenic and may have additive effects when combined.', NULL, 'Drugs that can lower the seizure threshold should preferably be withheld for at least 48 hours prior to and 24 hours following intrathecal administration of iodinated contrast media, provided that temporary interruption of therapy does not pose an undue risk to the patient.', NULL, 'Drugs that can lower the seizure threshold should preferably be withheld for at least 48 hours prior to and 24 hours following intrathecal administration of iodinated contrast media, provided that temporary interruption of therapy does not pose an undue risk to the patient. Otherwise, close monitoring is advised during and after contrast administration.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124682/', '[1] "Product Information. Amipaque (metrizamide)" Nycomed Inc, Princeton, NJ.[2] Hindmarsh T, Grepe A, Widen L "Metrizamide-phenothiazine interaction: report of a case with seizures following myelography." Acta Radiol Diagn (Stockh) 16 (1975): 129-35[3] "Product Information. Omnipaque 180 (iohexol)." Amersham Health, Princeton, NJ.[4] "Product Information. Osmovist 240 (iotrolan)." Berlex Canada Inc, Lachine, QC.[5] "Product Information. Isovue-M-200 (iopamidol)." Bracco Diagnostics Inc, Princeton, NJ.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (124533, 'Iron protein succinylate', 'Moxifloxacin', 'Moderate', 'INTERVAL: Oral preparations that contain magnesium, aluminum, or calcium may significantly decrease the gastrointestinal absorption of quinolone antibiotics. Absorption may also be reduced by sucralfate, which contains aluminum, as well as other polyvalent cations such as iron and zinc. The mechanism is chelation of quinolones by polyvalent cations, forming a complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', NULL, 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124683/', '[1] Teng R, Dogolo LC, Willavize SA, Friedman HL, Vincent J "Effect of Maalox and omeprazole on the bioavailability of trovafloxacin." J Antimicrob Chemother 39 Suppl B (1997): 93-7[2] Noyes M, Polk RE "Norfloxacin and absorption of magnesium-aluminum." Ann Intern Med 109 (1988): 168-9[3] Nix DE, Wilton JH, Ronald B, Distlerath L, Williams VC, Norman A "Inhibition of norfloxacin absorption by antacids." Antimicrob Agents Chemother 34 (1990): 432-5[4] "Product Information. Zagam (sparfloxacin)." Rhone-Poulenc Rorer, Collegeville, PA.[5] "Product Information. Factive (gemifloxacin)." GeneSoft Inc, San Francisco, CA.[6]