INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (124536, 'Moxifloxacin', 'Ketoprofen', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use. Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect. Patients with a history of seizures may be at greater risk.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124686/', '[1] "Product Information. Factive (gemifloxacin)." GeneSoft Inc, San Francisco, CA.[2] "Product Information. Avelox (moxifloxacin)" Bayer, West Haven, CT.[3] Davey PG "Overview of drug interactions with the quinolones." J Antimicrob Chemother 22(suppl c) (1988): 97-107[4] Domagala JM "Structure-activity and structure-side-effect relationships for the quinolone antibacterials." J Antimicrob Chemother 33 (1994): 685-706[5] "Product Information. Cipro (ciprofloxacin)." Bayer, West Haven, CT.[6] "Product Information. Floxin (ofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.[7] "Product Information. Tequin (gatifloxacin)" Bristol-Myers Squibb, Princeton, NJ.[8] Segev S. Rehavi M, Rubinstein E "Quinolones, theophylline, and diclofenac interactions with the gamma-aminobutyric acid receptor." Antimicrob Agents Chemother 32 (1988): 1624-6[9] Hooper DC, Wolfson JS "The fluoroquinolones: pharmacology, clinical uses, and toxicities in humans." Antimicrob Agents Chemother 28 (1985): 716-21[10] Ball P "Ciprofloxacin: an overview of adverse experiences." J Antimicrob Chemother 18 (1986): 187-93[11] "Product Information. Penetrex (enoxacin)." Rhone-Poulenc Rorer, Collegeville, PA.[12] "Product Information. Raxar (grepafloxacin)." Glaxo Wellcome, Research Triangle Park, NC.[13] Ball P, Tillotson G "Tolerability of fluoroquinolone antibiotics: past, present and future." Drug Saf 13 (1996): 343-8[14] "Product Information. Levaquin (levofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (124537, 'Moxifloxacin', 'Lactitol', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124687/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Schaefer DC, Cheskin LJ "Constipation in the elderly." Am Fam Physician 58 (1998): 907-14[4] Muller-Lissner SA "Adverse effects of laxatives: fact and fiction." Pharmacology 47 (1993): 138-45[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] Chin RL "Laxative-induced hypokalemia." Ann Emerg Med 32 (1998): 517-8', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (124538, 'Moxifloxacin', 'Lactobacillus acidophilus', 'Moderate', 'INTERVAL: Coadministration of oral probiotic preparations with oral antibiotics may reduce the efficacy of the probiotic. It has been theorized that concomitant antibiotics may kill the live organisms found in lactobacillus-, bacillus coagulans-, and bifidobacteria-containing oral probiotic preparations. However, the clinical significance of the interaction has not been established.', NULL, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', NULL, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124688/', '[1] Clarion Brands, Inc. "Florajen Probiotics. Available from: URL: https://www.florajen.com/products/florajen-women." ([2018]):', 1767363024);
