INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (124582, 'Moxifloxacin', 'Mifepristone', 'Major', 'Mifepristone may prolong the QTc interval in a dose-related manner. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'When mifepristone is used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, coadministration with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'When mifepristone is used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, coadministration with other drugs that can prolong the QT interval should generally be avoided. Caution is recommended if no alternatives exist and concomitant use is required. Serum potassium should be assessed prior to starting mifepristone and 1 to 2 weeks following initiation of therapy or an increase in dosage, and periodically as needed. Hypokalemia must be corrected prior to initiation of mifepristone. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hypokalemia such as fatigue, weakness, myalgia, muscle cramps, numbness, tingling, abdominal pain, constipation, palpitation, and irregular heart rhythm.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124732/', '[1] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[6] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.[7] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[8] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[9] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[10] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (124583, 'Moxifloxacin', 'Miglitol', 'Moderate', 'Quinolone antibiotics may interfere with the therapeutic effects of insulin and other antidiabetic agents. The use of quinolones has been associated with disturbances in blood glucose homeostasis possibly stemming from effects on pancreatic beta cell ATP-sensitive potassium channels that regulate insulin secretion. Both hyperglycaemia and hypoglycemia have been reported, usually in diabetic patients receiving concomitant treatment with an oral hypoglycemic agent (e.g., sulfonylurea) or insulin. Although hyperglycaemia is significantly more common and infection itself may be an underlying risk factor, hypoglycemia may cause greater morbidity and mortality.', NULL, 'Blood glucose should be closely monitored whenever quinolones are prescribed to diabetic patients, especially if they are elderly, have renal impairment, or are severely ill.', NULL, 'Blood glucose should be closely monitored whenever quinolones are prescribed to diabetic patients, especially if they are elderly, have renal impairment, or are severely ill. Patients should be apprised of the increased risk of dysglycemia and be particularly alert to potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypoglycemia occurs, patients should initiate appropriate remedial therapy immediately, discontinue the quinolone, and contact their physician. Alternative antibiotics may need to be considered.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124733/', 