INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (124711, 'Human adenovirus e serotype 4 strain cl-68578 antigen', 'Mycophenolate mofetil', 'Moderate', 'There are no data regarding the use of live, unattenuated Adenovirus Type 4 and Type 7 vaccine during treatment with immunosuppressive therapies, including alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. It is not known whether immunosuppressed patients may have an increased risk of adverse reactions or a decreased or suboptimal immunologic response to the vaccine.', NULL, 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies.', NULL, 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies. Caution is advised.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124861/', '[1] "Product Information. Adenovirus Vaccine Type 4 and Type 7 (adenovirus vaccine, live)." Teva Pharmaceuticals USA, North Wales, PA.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (124712, 'Aluminum hydroxide', 'Mycophenolate mofetil', 'Moderate', 'INTERVAL: Coadministration with antacids may decrease the oral bioavailability of mycophenolic acid. The exact mechanism of interaction is unknown but may involve chelation with polyvalent ions in antacids or enhanced drug solubility induced by an increase in gastric pH, or both. The interaction has been reported with magnesium/aluminum-containing antacids.', NULL, 'Patients treated with oral mycophenolic acid who require an antacid or antacid-containing preparation (e.', NULL, 'Patients treated with oral mycophenolic acid who require an antacid or antacid-containing preparation (e.g., didanosine buffered tablets or pediatric oral solution) should separate the administration times of the medications by at least two hours.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124862/', '[1] Lipsky JJ "Mycophenolate mofetil." Lancet 348 (1996): 1357-9[2] Bullingham R, Shah J, Goldblum R, Schiff M "Effects of food and antacid on the pharmacokinetics of single doses of mycophenolate mofetil in rheumatoid arthritis patients." Br J Clin Pharmacol 41 (1996): 513-6[3] "Product Information. Myfortic (mycophenolic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. CellCept (mycophenolate mofetil)." Roche Laboratories, Nutley, NJ.[5] "Product Information. Myfortic (mycophenolic acid)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (124713, 'Aminophylline', 'Mycophenolate mofetil', 'Minor', 'In vitro studies have shown that mycophenolic acid in concentrations up to 100 mcg/mL decreases protein binding of theophylline from 53% to 45%. The clinical significance of this interaction is unknown. Decreased protein binding theoretically could lead to increased plasma theophylline concentrations. However, clinical toxicity has not been reported. It may be appropriate to monitor more closely patients receiving theophylline or other methylxanthine derivatives with mycophenolic acid products. Patients should be advised to contact their physician if they experience potential symptoms of theophylline toxicity such as nausea, vomiting, diarrhea, headache, restlessness, insomnia, and irregular heartbeat.', NULL, '-', NULL, '-', 'Distribution', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124863/', '[1] "Product Information. CellCept (mycophenolate mofetil)." Roche Laboratories, Nutley, NJ.[2] "Product Information. Myfortic (mycophenolic acid)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (124714, 'Bacampicillin', 'Mycophenolate mofetil', 'Moderate', 'Theoretically, drugs that alter the gastrointestinal flora (e.g., antibiotics) may reduce the oral bioavailability of mycophenolic acid products. Some investigators believe that antimicrobials may interfere with the enterohepatic recirculation of mycophenolic acid by decreasing bacterial hydrolytic enzymes in the gastrointestinal tract that are responsible for regenerating mycophenolic acid from its glucuronide metabolite following first-pass metabolism.', NULL, 'Close clinical and laboratory monitoring for evidence of diminished immunosuppressive effect of mycophenolic acid is recommended during concomitant therapy with anti-infective agents.', NULL, 'Close clinical and laboratory monitoring for evidence of diminished immunosuppressive effect of mycophenolic acid is recommended during concomitant therapy with anti-infective agents.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124864/', '[1] "Product Information. CellCept (mycophenolate mofetil)." Roche Laboratories, Nutley, NJ.[2] "Product Information. Myfortic (mycophenolic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Myfortic (mycophenolic acid)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (124715, 'Bacillus calmette-guerin substrain tice live antigen', 'Mycophenolate mofetil', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124865/', '[1] Fauci AS, Braunwald E, Isselbacher KJ, Wilson JD, Martin JB, Kasper DL, Hauser SL, Longo DL, eds. "Harrison''s Principles of Internal Medicine. 14th ed." New York, NY: McGraw-Hill Health Professionals Division (1998):[2] "Product Information. YF-Vax (yellow fever vaccine)." sanofi pasteur, Swiftwater, PA.[3] "Product Information. Attenuvax (measles virus vaccine, live, attenuated)." Merck & Co, Inc, West Point, PA.[4] Kriner P, Lopez K, Leung J, Harpaz R, Bialek SR "Notes from the field: varicella-associated death of a vaccinated child with leukemia - California, 2012." MMWR Morb Mortal Wkly Rep 63 (2014): 161[5] "Product Information. M-M-R II (measles/mumps/rubella virus vaccine)." Merck & Company Inc, West Point, PA.[6] Charkoudian LD, Kaiser GM, Steinmetz RL, Srivastava SK "Acute retinal necrosis after herpes zoster vaccination." Arch Ophthalmol 129 (2011): 1495-7[7] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[8] "Product Information. Meruvax II (rubella virus vaccine, live)." Merck & Co, Inc, West Point, PA.[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (124716, 'Bifidobacterium longum infantis', 'Mycophenolate mofetil', 'Moderate', 'Probiotic use during immunosuppressant or intense antineoplastic therapy may theoretically increase the risk of infections from the live microorganisms contained in probiotic products. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy.', NULL, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy. It may be advisable to avoid using probiotics, particularly products containing saccharomyces boulardii, in patients who are significantly immunosuppressed.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124866/', '[1] Borriello SP, Hammes WP, Holzapfel W, et.al "Safety of probiotics that contain lactobacilli or bifidobacteria." Clin Infect Dis 36 (2003): 775-80[2] Salminen MK, Tynkkynen S, Rautelin H, et al. "Lactobacillus bacteremia during a rapid increase in probiotic use of Lactobacillus rhamnosus GG in Finland." Clin Infect Dis 35 (2002): 1155-60[3] Schlegel L, Lemerle S, Geslin P "Lactobacillus species as opportunistic pathogens in immunocompromised patients." Eur J Clin Microbiol Infect Dis 17 (1998): 887-8[4] Salminen MK, Rautelin H, Tynkkynen S, et al. "Lactobacillus bacteremia, clinical significance, and patient outcome, with special