INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (124753, 'Ganciclovir', 'Mycophenolate mofetil', 'Moderate', 'Data from a single-dose study do not suggest the potential for a clinically significant pharmacokinetic interaction between mycophenolic acid and ganciclovir in patients with normal renal function. However, since ganciclovir and the glucuronide metabolite of mycophenolic acid are both eliminated primarily by the kidney, plasma levels of both may increase in renal impairment due to competition for renal tubular secretion. In addition, use of mycophenolic acid and ganciclovir may increase the risk and severity of hematologic toxicity due to additive myelosuppressive effects.', NULL, 'Caution is advised if ganciclovir or its prodrug, valganciclovir, must be used concomitantly with mycophenolic acid products in patients with impaired renal function.', NULL, 'Caution is advised if ganciclovir or its prodrug, valganciclovir, must be used concomitantly with mycophenolic acid products in patients with impaired renal function. Close monitoring for toxicities such as myelo- and immunosuppression is recommended.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124903/', '[1] "Product Information. Valcyte (valganciclovir)" Roche Laboratories, Nutley, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Wolfe EJ, Mathur V, Tomlanovich S, Jung D, Wong R, Griffy K, Aweeka FT "Pharmacokinetics of mycophenolate mofetil and intravenous ganciclovir alone and in combination in renal transplant recipients." Pharmacotherapy 17 (1997): 591-8[4] "Product Information. CellCept (mycophenolate mofetil)." Roche Laboratories, Nutley, NJ.[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Myfortic (mycophenolic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[7] Brown F, Banken L, Saywell K, Arum I "Pharmacokinetics of valganciclovir and ganciclovir following multiple oral dosages of valganciclovir in HIV- and CMV-seropositiv volunteers." Clin Pharmacokinet 37 (1999): 167-76[8] "Product Information. Valcyte (valganciclovir)" Roche Laboratories, Nutley, NJ.[9] "Product Information. Myfortic (mycophenolic acid)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (124754, 'Gatifloxacin', 'Mycophenolate mofetil', 'Moderate', 'Theoretically, drugs that alter the gastrointestinal flora (e.g., antibiotics) may reduce the oral bioavailability of mycophenolic acid products. Some investigators believe that antimicrobials may interfere with the enterohepatic recirculation of mycophenolic acid by decreasing bacterial hydrolytic enzymes in the gastrointestinal tract that are responsible for regenerating mycophenolic acid from its glucuronide metabolite following first-pass metabolism.', NULL, 'Close clinical and laboratory monitoring for evidence of diminished immunosuppressive effect of mycophenolic acid is recommended during concomitant therapy with anti-infective agents.', NULL, 'Close clinical and laboratory monitoring for evidence of diminished immunosuppressive effect of mycophenolic acid is recommended during concomitant therapy with anti-infective agents.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124904/', '[1] "Product Information. CellCept (mycophenolate mofetil)." Roche Laboratories, Nutley, NJ.[2] "Product Information. Myfortic (mycophenolic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Myfortic (mycophenolic acid)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363024);
