INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (124841, 'Ustekinumab', 'Mycophenolate mofetil', 'Moderate', 'The use of ustekinumab with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', NULL, 'Caution is advised if ustekinumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.', NULL, 'Caution is advised if ustekinumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124991/', '[1] "Product Information. Stelara (ustekinumab)." Centocor Inc, Malvern, PA.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (124842, 'Valaciclovir', 'Mycophenolate mofetil', 'Minor', 'Coadministration with mycophenolic acid may increase the plasma concentrations of acyclovir. The mechanism of interaction has not been described. However, because both MPAG and acyclovir plasma concentrations are increased in the presence of renal impairment, the potential exists for the two drugs to compete for active tubular secretion, further increasing the concentrations of both drugs. Caution may be appropriate in patients with decreased renal function.', NULL, '-', NULL, '-', 'Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124992/', '[1] "Product Information. Myfortic (mycophenolic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. CellCept (mycophenolate mofetil)." Roche Laboratories, Nutley, NJ.[3] Gimenez F, Foeillet E, Bourdon O, et al. "Evaluation of pharmacokinetic interactions after oral administration of mycophenolate mofetil and valaciclovir or aciclovir to healthy subjects." Clin Pharmacokinet 43 (2004): 685-92[4] "Product Information. Myfortic (mycophenolic acid)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (124843, 'Valganciclovir', 'Mycophenolate mofetil', 'Moderate', 'Data from a single-dose study do not suggest the potential for a clinically significant pharmacokinetic interaction between mycophenolic acid and ganciclovir in patients with normal renal function. However, since ganciclovir and the glucuronide metabolite of mycophenolic acid are both eliminated primarily by the kidney, plasma levels of both may increase in renal impairment due to competition for renal tubular secretion. In addition, use of mycophenolic acid and ganciclovir may increase the risk and severity of hematologic toxicity due to additive myelosuppressive effects.', NULL, 'Caution is advised if ganciclovir or its prodrug, valganciclovir, must be used concomitantly with mycophenolic acid products in patients with impaired renal function.', NULL, 'Caution is advised if ganciclovir or its prodrug, valganciclovir, must be used concomitantly with mycophenolic acid products in patients with impaired renal function. Close monitoring for toxicities such as myelo- and immunosuppression is recommended.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124993/', '[1] "Product Information. Valcyte (valganciclovir)" Roche Laboratories, Nutley, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Wolfe EJ, Mathur V, Tomlanovich S, Jung D, Wong R, Griffy K, Aweeka FT "Pharmacokinetics of mycophenolate mofetil and intravenous ganciclovir alone and in combination in renal transplant recipients." Pharmacotherapy 17 (1997): 591-8[4] "Product Information. CellCept (mycophenolate mofetil)." Roche Laboratories, Nutley, NJ.[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Myfortic (mycophenolic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[7] Brown F, Banken L, Saywell K, Arum I "Pharmacokinetics of valganciclovir and ganciclovir following multiple oral dosages of valganciclovir in HIV- and CMV-seropositiv volunteers." Clin Pharmacokinet 37 (1999): 167-76[8] "Product Information. Valcyte (valganciclovir)" Roche Laboratories, Nutley, NJ.[9] "Product Information. Myfortic (mycophenolic acid)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363024);
