    ) VALUES (1398997, 'Glyburide', 'Verapamil', 'Minor', 'The concomitant use of verapamil therapy may modestly increase serum levels of glyburide; however, glucose lowering effects are not altered. The mechanism has not been determined.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
