        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1398999, 'Guanabenz', 'Verapamil', 'Moderate', 'The hypotensive effects of some calcium channel blockers and alpha-adrenergic blockers may be additive. The proposed mechanism is additive vasodilatory effects and inhibition of CYP450 hepatic metabolism of some alpha blockers by certain calcium channel blockers.', NULL, 'Hemodynamic responses should be monitored during coadministration, especially during the first few weeks of therapy.', NULL, 'Hemodynamic responses should be monitored during coadministration, especially during the first few weeks of therapy. Patients should be advised to take the alpha-blocker at bedtime and to notify their physician if they experience dizziness or syncope while awake.', 'Metabolism, Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
