    ) VALUES (1399042, 'Verapamil', 'Lemborexant', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of lemborexant, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of lemborexant with potent or moderate CYP450 3A4 inhibitors should be avoided.', NULL, 'Concomitant use of lemborexant with potent or moderate CYP450 3A4 inhibitors should be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
