        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1399051, 'Verapamil', 'Lisinopril', 'Minor', 'Calcium channel blockers and angiotensin converting enzyme (ACE) inhibitors may have additive hypotensive effects. While these drugs are often safely used together, careful monitoring of the systemic blood pressure is recommended during coadministration, especially during the first one to three weeks of therapy.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
