        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1399052, 'Verapamil', 'Lithium carbonate', 'Moderate', 'Verapamil has variable effects on lithium levels. Increases, decreases, and no change in serum lithium levels have all been reported. The mechanism is unknown.', NULL, 'Patients who must take both drugs should be monitored for evidence of lithium toxicity.', NULL, 'Patients who must take both drugs should be monitored for evidence of lithium toxicity. Patients should be advised to notify their physician if they experience symptoms of possible lithium toxicity such as drowsiness, dizziness, confusion, ataxia, vomiting, diarrhea, thirst, blurry vision, tinnitus, or tremor.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
