        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1399110, 'Verapamil', 'Niraparib', 'Moderate', 'Coadministration with potent inhibitors of P-glycoprotein (P-gp) may increase the bioavailability of niraparib, which is a substrate of the efflux transporter. Increased levels of niraparib may lead to increased incidence of hematological toxicity (e.g., thrombocytopenia, anemia, neutropenia) and/or cardiovascular toxicity (e.g., hypertension, hypertensive crisis).', NULL, 'Pharmacologic response to niraparib should be monitored more closely whenever a potent P-gp inhibitor is added to or withdrawn from therapy, and the niraparib dosage adjusted as necessary.', NULL, 'Pharmacologic response to niraparib should be monitored more closely whenever a potent P-gp inhibitor is added to or withdrawn from therapy, and the niraparib dosage adjusted as necessary.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
