        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1399112, 'Verapamil', 'Nitroglycerin', 'Moderate', 'Marked, symptomatic orthostatic hypotension has been reported when calcium channel blockers were used in combination with oral controlled-release nitroglycerin.', NULL, 'Dosage adjustments of either class of agents may be necessary.', NULL, 'Dosage adjustments of either class of agents may be necessary. All patients treated with nitroglycerin--with or without calcium channel blockers--should be made aware of possible adverse effects such as dizziness, lightheadedness, and orthostasis, and advised to arise slowly from a sitting or recumbent position.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
