        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1399113, 'Verapamil', 'Nortriptyline', 'Moderate', 'Concurrent administration of verapamil may increase tricyclic antidepressant (TCA) serum concentrations. The mechanism appears to be inhibition of CYP450 metabolism. Pharmacologic and toxic effects of TCAs may be increased.', NULL, 'Close monitoring for clinical response and tolerance is recommended whenever verapamil is added to or discontinued from an antidepressant regimen.', NULL, 'Close monitoring for clinical response and tolerance is recommended whenever verapamil is added to or discontinued from an antidepressant regimen. Patients should be advised to notify their physician if they experience excessive antidepressant effects such as dry mouth, blurry vision, irregular or fast heartbeat, constipation, urinary retention, dizziness, or orthostatic hypotension. Dose adjustments may be necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
