        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1399137, 'Verapamil', 'Pentoxifylline', 'Moderate', 'Pentoxifylline may potentiate the action of hypotensive agents. Small decreases in blood pressure have been observed in some patients treated with pentoxifylline plus nifedipine or captopril. The mechanism of interaction has not been described.', NULL, 'Caution is advised if pentoxifylline is used in combination with hypotensive agents.', NULL, 'Caution is advised if pentoxifylline is used in combination with hypotensive agents. Periodic systemic blood pressure monitoring is recommended.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
