        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1399152, 'Verapamil', 'Ponatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ponatinib, which is a substrate of the isoenzyme.', NULL, 'Caution is advised if ponatinib is used in combination with moderate CYP450 3A4 inhibitors.', NULL, 'Caution is advised if ponatinib is used in combination with moderate CYP450 3A4 inhibitors. Patients should be monitored for potentially increased side effects such as thromboembolism, ischemia, congestive heart failure, hypertension, hepatotoxicity, pancreatitis, myelosuppression, hemorrhage, and fluid retention.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
