        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1399183, 'Verapamil', 'Rifabutin', 'Moderate', 'Rifampin may decrease the bioavailability, plasma levels, and pharmacological effects of verapamil. The mechanism is induction of CYP450 3A4 hepatic metabolism by rifampin.', NULL, 'Patients should be closely monitored for clinical efficacy of verapamil during coadministration, and conversely, for excessive calcium channel blockade when the rifamycin is discontinued.', NULL, 'Patients should be closely monitored for clinical efficacy of verapamil during coadministration, and conversely, for excessive calcium channel blockade when the rifamycin is discontinued. Alternative medications may be considered. Patients should be advised to notify their physician if they experience a worsening of their symptoms (e.g., angina, arrhythmia).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
