    ) VALUES (1399286, 'Vilazodone', 'Verapamil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of vilazodone, which is primarily metabolized by the isoenzyme.', NULL, 'When used with moderate CYP450 3A4 inhibitors, the maintenance dosage of vilazodone should be reduced to 20 mg/day in patients who develop intolerable adverse events.', NULL, 'When used with moderate CYP450 3A4 inhibitors, the maintenance dosage of vilazodone should be reduced to 20 mg/day in patients who develop intolerable adverse events.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
