    ) VALUES (1399287, 'Vincristine', 'Verapamil', 'Minor', 'In vitro tests have shown that verapamil displaces vincristine from protein-binding sites--particularly alpha(1) -acid glycoprotein, or AGP. The clinical significance is unknown. If verapamil and vincristine are used concomitantly, close neurological observation is advised.', NULL, '-', NULL, '-', 'Distribution', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
