INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (126271, 'Ramucirumab', 'Ketoprofen', 'Major', 'Theoretical concerns exist that nonsteroid anti-inflammatory drugs (NSAIDs) and salicylates may potentiate the risk of bleeding and gastrointestinal perforation associated with ramucirumab therapy. Ramucirumab can cause bleeding, including severe and sometimes fatal hemorrhagic complications.', NULL, 'Caution is advised if ramucirumab must be used with chronic NSAID or salicylate therapy.', NULL, 'Caution is advised if ramucirumab must be used with chronic NSAID or salicylate therapy. Close clinical and laboratory observation for bleeding complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools. Ramucirumab should be permanently discontinued in patients who experience severe bleeding or gastrointestinal perforation.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126423/', '[1] "Product Information. Cyramza (ramucirumab)." Eli Lilly and Company, Indianapolis, IN.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (126272, 'Ranitidine (bismuth citrate)', 'Ketoprofen', 'Minor', 'H2 antagonists may alter the disposition of nonsteroidal anti-inflammatory drugs (NSAIDs), resulting in increased or decreased plasma concentrations. Data are varied, even for the same NSAID. The mechanism may be related to inhibition of metabolism, changes in gastric pH that decrease absorption, and/or reduced urinary elimination. Statistically significant changes have been small and of limited clinical significance. Clinical monitoring of patient response and tolerance is recommended.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126424/', '[1] Said SA, Foda AM "Influence of cimetidine on the pharmacokinetics of piroxicam in rat and man." Arzneimittelforschung 39 (1989): 790-2[2] "Product Information. Daypro (oxaprozin)." Searle, Skokie, IL.[3] Scavone JM, Greenblatt DJ, Matlis R, Harmatz JS "Interaction of oxaprozin with acetaminophen, cimetidine, and ranitidine." Eur J Clin Pharmacol 31 (1986): 371-4', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (126273, 'Regorafenib', 'Ketoprofen', 'Major', 'Coadministration of regorafenib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with regorafenib is associated with a high frequency of hemorrhage and thrombocytopenia. Coadministration of regorafenib with strong UGT1A9 inhibitors such as mefenamic acid or diflunisal may theoretically increase systemic exposure to regorafenib.', NULL, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with regorafenib.', NULL, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with regorafenib. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The INR should be monitored more frequently during coadministration of warfarin.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126425/', '[1] "Product Information. Stivarga (regorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[4] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.[5] "Product Information. Stivarga (regorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (126274, 'Remdesivir', 'Ketoprofen', 'Moderate', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury. Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', NULL, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.', NULL, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible. Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate. Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline. Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126426/', '[1] Gilead Sciences, Inc "About Remdesivir. Available from: URL: https://www.gilead.com/purpose/advancing-global-health/covid-19/about-remdesivir." ([2020]):[2] US Food and Drug Administration "Fact sheet for health care providers emergency use authorization (EUA) of remdesivir (GS-5734TM) Available from: URL: https://www.fda.gov/media/137566/download." ([2020, May 1]]):[3] European Medicines Agency "Summary on compassionate use. Remdesivir Gilead. Available from: URL: https://www.ema.europa.eu/en/documents/other/summary-compassionate-use-remdesivir-gilead_en.pdf." ([2020 Apr 03]):[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363024);
