INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (126292, 'Tazemetostat', 'Ketoprofen', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126444/', '[1] "Product Information. Nevanac (nepafenac ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[2] "Product Information. Acular (ketorolac)." Allergan Inc, Irvine, CA.[3] "Product Information. Xibrom (bromfenac ophthalmic)." ISTA Pharmaceuticals, Irvine, CA.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (126293, 'Telbivudine', 'Ketoprofen', 'Moderate', 'Because telbivudine is primarily eliminated by renal excretion, coadministration with drugs that affect renal function may alter the plasma concentrations of telbivudine and/or the coadministered drug.', NULL, 'Caution is advised if telbivudine is prescribed in combination with potentially nephrotoxic drugs (e.', NULL, 'Caution is advised if telbivudine is prescribed in combination with potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be evaluated prior to and during therapy with telbivudine.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126445/', '[1] "Product Information. Tyzeka (telbivudine)." IDEC Pharmaceuticals Corporation, San Diego, CA.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (126294, 'Telmisartan', 'Ketoprofen', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of angiotensin II receptor antagonists. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Concomitant use of NSAIDs and angiotensin II receptor antagonists may cause deterioration in renal function, particularly in patients who are elderly or volume-depleted (including those on diuretic therapy) or have compromised renal function.', NULL, 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. Renal function should also be evaluated periodically during prolonged coadministration. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126446/', '[1] Radack KL, Deck CC, Bloomfield SS "Ibuprofen interferes with the efficacy of antihypertensive drugs: a randomized, double-blind, placebo-controlled trial of ibuprofen compared with acetaminophen." Ann Intern Med 107 (1987): 628-35[2] "Multum Information Services, Inc. Expert Review Panel"[3] "Product Information. Toradol (ketorolac)." Roche Laboratories, Nutley, NJ.[4] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.[5] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.', 1767363024);
