INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (126335, 'Glucarpidase', 'Levoleucovorin', 'Moderate', 'INTERVAL: Concomitant administration of glucarpidase may decrease the plasma concentrations of leucovorin, which is a substrate for glucarpidase. Conversely, leucovorin may interfere with the action of glucarpidase in the treatment of toxic plasma methotrexate concentrations in patients with impaired renal function by reducing the availability of glucarpidase, a recombinant bacterial enzyme that provides an alternate nonrenal pathway for methotrexate elimination by converting methotrexate to an inactive metabolite and glutamate.', NULL, 'Leucovorin should be continued following treatment with glucarpidase; however, it should not be administered within two hours before or two hours after a dose of glucarpidase.', NULL, 'Leucovorin should be continued following treatment with glucarpidase; however, it should not be administered within two hours before or two hours after a dose of glucarpidase. For the first 48 hours after glucarpidase administration, the same leucovorin dose as given prior to glucarpidase treatment is appropriate. Beyond 48 hours, the leucovorin dose should be based on the measured methotrexate concentration. Do not discontinue leucovorin based on the determination of a single methotrexate concentration below the leucovorin treatment threshold. Therapy with leucovorin should be continued until the methotrexate concentration has been maintained below the leucovorin treatment threshold for a minimum of 3 days. Hydration and alkalinization of the urine should be continued as indicated.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126487/', '[1] "Product Information. Voraxaze (glucarpidase)." BTG International Inc, West Conshohocken, PA.', 1767363024);
