INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (126415, 'Atracurium', 'Labetalol', 'Moderate', 'Neuromuscular blocking agents and beta-blockers may have additive cardiovascular depressant effects. Limited data suggest that beta-blockers may modestly potentiate the pharmacologic effects of neuromuscular blocking agents. The exact mechanism of interaction is unknown but may involve effects in the postsynaptic membrane, as beta-blockers alone have been reported to exacerbate or unmask myasthenia gravis.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers.', NULL, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers. Respiratory and cardiovascular status should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126568/', '[1] Loan PB, Connolly FM, Mirakhur RK, Kumar N, Farling P "Neuromuscular effects of rocuronium in patients receiving beta-adrenoreceptor blocking, calcium entry blocking and anticonvulsant drugs." Br J Anaesth 78 (1997): 90-1[2] Harrah MD, Way WL, Katzung BG "The interaction of d-tubocurarine with antiarrhythmic drugs." Anesthesiology 33 (1970): 406-10[3] Rozen MS, Whan FM "Prolonged curarization associated with propranolol." Med J Aust 1 (1972): 467-8[4] Murthy VS, Patel KD, Elangovan RG, Hwang TF, Solochek SM, Steck JD, Laddu AR "Cardiovascular and neuromuscular effects of esmolol during induction of anesthesia." J Clin Pharmacol 26 (1986): 351-7[5] Glynne GL "Drug interaction?" Anaesthesia 39 (1984): 293[6] Herishanu Y, Rosenberg P "Beta-blockers and myasthenia gravis." Ann Intern Med 83 (1975): 834-5[7] Yate B, Mostafa SM "Drug interaction?" Anaesthesia 39 (1984): 728-9[8] Chapple DJ, Clark JS, Hughes R "Interaction between atracurium and drugs used in anaesthesia." Br J Anaesth 55 Suppl 1 (1983): s17-22', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (126416, 'Attapulgite', 'Labetalol', 'Minor', 'Concurrent administration with aluminum and magnesium antacids has been shown to decrease the oral bioavailability of certain beta-blockers, although data are conflicting. The exact mechanism of interaction is unknown but may involve cation binding of beta-blockers or a reduction in the dissolution rate due to increased gastric pH. Based on available data, the clinical significance of this potential interaction is difficult to determine. As a precaution, patients may want to consider separating the administration times of beta-blockers and antacids or other aluminum- or magnesium-containing products by at least 2 hours.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126569/', '[1] D''Arcy PF, McElnay JC "Drug-antacid interactions: assessment of clinical importance." Drug Intell Clin Pharm 21 (1987): 607-17[2] Regardh CG, Lundborg P, Persson BA "The effect of antacid, metoclopramide, and propantheline on the bioavailability of metoprolol and atenolol." Biopharm Drug Dispos 2 (1981): 79-87[3] Hong CY, Hu SC, Lin SJ, Chiang BN "Lack of influence of aluminum hydroxide on the bioavailability and beta-adrenoceptor blocking activity of propranolol." Int J Clin Pharmacol Ther Toxicol 23 (1985): 244-6[4] Kirch W, Schafer-Korting M, Axthelm T, Kohler H, Mutschler E "Interaction of atenolol with furosemide and calcium and aluminum salts." Clin Pharmacol Ther 30 (1981): 429-35[5] Laer S, Neumann J, Scholz H "Interaction between sotalol and an antacid preparation." Br J Clin Pharmacol 43 (1997): 269-72[6] Gugler R, Allgayer H "Effects of antacids on the clinical pharmacokinetics of drugs. An update." Clin Pharmacokinet 18 (1990): 210-9[7] Dobbs JH, Skoutakis VA, Acchiardo SR, Dobbs BR "Effects of aluminum hydroxide on the absorption of propranolol." Curr Ther Res Clin Exp 21 (1977): 887-92', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (126417, 'Bedaquiline', 'Labetalol', 'Moderate', 'Coadministration of bedaquiline with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; other antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; ketolide and macrolide antibiotics; interferons; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), especially in patients with diminished hepatic reserve. Patients treated with bedaquiline should have serum ALT, AST, alkaline phosphatase, and bilirubin monitored at baseline and monthly during treatment, or as often as needed. An increase of serum aminotransferases to greater than 3 times ULN should be followed by repeat testing within 48 hours. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Discontinue bedaquiline if aminotransferase elevations are accompanied by total bilirubin elevation greater than 2 times ULN, aminotransferase elevations are greater than 8 times ULN, or aminotransferase elevations persist beyond 2 weeks.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126570/', '[1] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[3] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (126418, 'Belladonna', 'Labetalol', 'Moderate', 'Anticholinergic agents frequently cause drowsiness and other central nervous system-depressant effects that may be additive with those induced by beta blockers. In addition, these agents may increase heart rate and theoretically may counteract the bradycardic effects of beta blockers. Pharmacokinetically, anticholinergic agents may delay the gastrointestinal absorption of beta blockers and other drugs that are administered orally. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents.', NULL, 'Patients should be monitored for potentially excessive CNS adverse effects (e.', NULL, 'Patients should be monitored for potentially excessive CNS adverse effects (e.g., drowsiness, dizziness, lightheadedness, confusion, blurred vision) if anticholinergic agents are used in combination with beta blockers. Patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism, Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126571/', '[1] Regardh CG, Lundborg P, Persson BA "The effect of antacid, metoclopramide, and propantheline on the bioavailability of metoprolol and atenolol." Biopharm Drug Dispos 2 (1981): 79-87[2] Briant RH, Dorrington RE, Ferry DG, Paxton JW "Bioavailability of metoprolol in young adults and the elderly, with additional studies on the effects of metoclopramide and probanthine." Eur J Clin Pharmacol 25 (1983): 353-6[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Clark JM, Seager SJ "Gastric emptying following premedication with glycopyrrolate or atropine." Br J Anaesth 55 (1983): 1195-9', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (126419, 'Benzthiazide', 'Labetalol', 'Moderate', 'Although they are often combined in clinical practice, diuretics and beta-blockers may increase the risk of hyperglycemia and hypertriglyceridemia in some patients, especially in patients with diabetes or latent diabetes. In addition, the risk of QT interval prolongation and arrhythmias (e.g. torsades de pointes) due to sotalol may be increased by potassium-depleting diuretics.', NULL, 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration.', NULL, 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration. Patients should be advised to seek medical assistance if they experience dizziness, weakness, fainting, fast or irregular heartbeats, or loss of blood glucose control.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126572/', '[1] Dean S, Kendall MJ, Potter S, Thompson MH, Jackson DA "Nadolol in combination with indapamide and xipamide in resistant hypertensives." Eur J Clin Pharmacol 28 (1985): 29-33[2] Dornhorst A, Powell SH, Pensky J "Aggravation by propranolol of hyperglycaemic effect of hydrochlorothiazide in type II diabetics without alteration of insulin secretion." Lancet 1 (1985): 123-6[3] "Product Information. Lozol (indapamide)." Rhone-Poulenc Rorer, Collegeville, PA.[4] Marcy TR, Ripley TL "Aldosterone antagonists in the treatment of heart failure." Am J Health Syst Pharm 63 (2006): 49-58[5] Roux A, Le Liboux A, Delhotal B, Gaillot J, Flouvat B "Pharmacokinetics in man of acebutolol and hydrochlorothiazide as single agents and in combination." Eur J Clin Pharmacol 24 (1983): 801-6', 1767363024);
