INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (126472, 'Galantamine', 'Labetalol', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126626/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Cognex (tacrine)." Parke-Davis, Morris Plains, NJ.[3] Tiseo PJ, Perdomo CA, Friedhoff LT "Concurrent administration of donepezil HCI and digoxin: assessment of pharmacokinetic changes." Br J Clin Pharmacol 46(Suppl 1) (1998): 40-4[4] Tavassoli N, Sommet A, Lapeyre-Mestre M, Bagheri H, Montrastruc JL "Drug interactions with cholinesterase inhibitors : an analysis of the French pharmacovigilance database and a comparison of two national drug formularies (vidal, british national formulary)." Drug Saf 30 (2007): 1063-71[5] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (126473, 'Garlic', 'Labetalol', 'Minor', 'Garlic has been found in some studies to lower blood pressure and may theoretically potentiate the effects of hypotensive agents. A meta-analysis of 11 randomized, placebo-controlled studies showed a mean decrease of 4.6 mmHg for systolic blood pressure and no significant effect for diastolic blood pressure in subjects treated with garlic (most often garlic powder 600 to 900 mg/day, providing 3.6 to 5.4 mg of the active component allicin) compared to placebo.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126627/', '[1] Tattelman E "Health effects of garlic." Am Fam Physician 72 (2005): 103-6[2] Richard CL, Jurgens TM "Effects of natural health products on blood pressure." Ann Pharmacother 39 (2005): 712-20', 1767363024);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (126474, 'Glipizide', 'Labetalol', 'Moderate', 'Beta-blockers may inhibit some of the normal physiologic response to hypoglycemia. Symptoms of hypoglycemia such as tremor and tachycardia may be absent, making it more difficult for patients to recognize an oncoming episode. Specifically, inhibition of catecholamine-mediated glycogenolysis and glucose mobilization in association with beta-blockade can potentiate insulin-induced hypoglycemia in diabetics and delay the recovery of normal blood glucose levels. Prolonged and severe hypoglycemia may occur, although these events have rarely been reported. Significant increases in blood pressure and bradycardia can also occur during hypoglycemia in diabetics treated with insulin and beta-blockers due to antagonism of epinephrine''s effect on beta-2 adrenergic receptors, which leads to unopposed alpha-adrenergic effects including vasoconstriction. Other effects reported with various beta-blockers include decreased glucose tolerance and decreased glucose-induced insulin secretion.', NULL, 'In general, cardioselective beta-blockers are considered safer than noncardioselective agents in the treatment of diabetic patients.', NULL, 'In general, cardioselective beta-blockers are considered safer than noncardioselective agents in the treatment of diabetic patients. Patients should be advised of the need for regular blood glucose monitoring and be aware that certain symptoms of hypoglycemia such as tremor and tachycardia may be masked. However, other symptoms such as headache, dizziness, drowsiness, confusion, nausea, hunger, weakness, and perspiration may be unaffected.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126628/', '[1] Zaman R, Kendall MJ, Biggs PI "The effect of acebutolol and propranolol on the hypoglycaemic action of glibenclamide." Br J Clin Pharmacol 13 (1982): 507-12[2] Shepherd AM, Lin M-S, Keeton TK "Hypoglycemia-induced hypertension in a diabetic patient on metoprolol." Ann Intern Med 94 (1981): 357-8[3] Viberti GC, Keen H, Bloom SR "Beta blockade and diabetes mellitus: effect of oxprenolol and metoprolol on the metabolic, cardiovascular, and hormonal response to insulin-induced hypoglycemia in normal subjects." Metabolism 29 (1980): 866-72[4] Viberti GC, Keen H, Bloom SR "Beta blockade and diabetes mellitus: effect of oxprenolol and metoprolol on the metabolic, cardiovascular, and hormonal response to insulin-induced hypoglycemia in insulin-dependent diabetics." Metabolism 29 (1980): 873-9[5] Ostman J "B-adrenergic blockade and diabetes mellitus." Acta Med Scand 672 (1983): 69-77[6] Groop L, Totterman KJ, Harno K, Gordin A "Influence of beta-blocking drugs on glucose metabolism in patients with non-insulin dependent diabetes mellitus." Acta Med Scand 211 (1982): 7-12[7] Sinclair AJ, Davies IB, Warrington SJ "Betaxolol and glucose-insulin relationships: studies in normal subjects taking glibenclamide or metformin." Br J Clin Pharmacol 30 (1990): 699-702[8] Popp DA, Tse TF, Shah SD, et al "Oral propranolol and metoprolol both impair glucose recovery from insulin-induced hypoglycemia in insulin-dependent diabetes mellitus." Diabetes Care 7 (1984): 243-7[9] Mann SJ, Krakoff LR "Hypertensive crisis caused by hypoglycemia and propranolol." Arch Intern Med 144 (1984): 2427-8[10] Deacon SP, Karunanayake A, Barnett D "Acebutolol, atenolol, and propranolol and metabolic responses to acute hypoglycaemia in diabetes." Br Med J 12 (1977): 1255-7[11] Pollare T, Lithell H, Selinus I, Berne C "Sensitivity to insulin during treatment with atenolol and metoprolol: a randomised, double blind study of effects on c