INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252315, 'Elexacaftor', 'Valsartan', 'Moderate', 'Coadministration with elexacaftor may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptides (OATP) 1B1 and 1B3.  The proposed mechanism, based on in vitro data, involves decreased metabolic clearance due to inhibition of OATP 1B1- and 1B3-mediated hepatic uptake by elexacaftor and its metabolite.', NULL, 'Caution is advised when elexacaftor is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when elexacaftor is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever elexacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Amlodipine, Irbesartan, Hydrochlorothiazide, Losartan, Azilsartan medoxomil, Nebivolol, Telmisartan, Candesartan, Sacubitril, Aliskiren, Eprosartan, More', 'Nitric Oxide, Doxapram, Ivacaftor, Lumacaftor', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299305/', '[1] "Product Information. Trikafta (elexacaftor/ivacaftor/tezacaftor)." Vertex Pharmaceuticals  (2019):', 1767369485);
