INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252327, 'Empagliflozin', 'Nitroprusside', 'Moderate', 'Sodium-glucose co-transporter 2 (SGLT-2) inhibitors may potentiate the hypotensive effects of diuretics and other antihypertensive agents or vasodilators.  Inhibition of glucose and sodium co-transport produces mild diuresis and transient natriuresis, resulting in intravascular volume contraction.', NULL, 'Caution is advised if SGLT-2 inhibitors are coadministered with diuretics and other hypotensive agents, particularly in the elderly and patients with impaired renal function.', NULL, 'Caution is advised if SGLT-2 inhibitors are coadministered with diuretics and other hypotensive agents, particularly in the elderly and patients with impaired renal function.  Prior to initiating SGLT-2 inhibitors, volume status should be assessed and corrected, if necessary.  Clinical and laboratory monitoring are recommended during therapy, including electrolytes, fluid status, renal function, and blood pressure.  If volume depletion occurs, treatment with SGLT-2 inhibitors should be interrupted until the condition is corrected.', 'Synergism', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Tolazamide, Dulaglutide, Exenatide, Tolbutamide, Miglitol, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299325/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Invokana (canagliflozin)." Janssen Pharmaceuticals  (2013):[4] "Product Information. Farxiga (dapagliflozin)." Bristol-Myers Squibb  (2014):[5] "Product Information. Jardiance (empagliflozin)." Boehringer Ingelheim  (2014):[6] "Product Information. Brenzavvy (bexagliflozin)." TheracosBio, LLC  (2023):', 1767369485);
