INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252572, 'Glecaprevir', 'Atazanavir', 'Major', 'Coadministration with atazanavir plus ritonavir may significantly increase the plasma concentrations of glecaprevir.  The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of glecaprevir by atazanavir and ritonavir.  Inhibition of P-glycoprotein-mediated intestinal efflux and CYP450 3A4-mediated metabolism of glecaprevir may also contribute.', NULL, 'Concomitant use of glecaprevir with atazanavir is considered contraindicated.', NULL, 'Concomitant use of glecaprevir with atazanavir is considered contraindicated.', 'Metabolism, Absorption, Distribution', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Amprenavir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, More', 'Cabotegravir, Amprenavir, Zanamivir, Velpatasvir, Rilpivirine, Oseltamivir, Cobicistat, Remdesivir, Famciclovir, Darunavir, Maribavir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299943/', '[1] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical  (2017):', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252573, 'Glecaprevir', 'Atorvastatin', 'Major', 'Coadministration with glecaprevir-pibrentasvir may significantly increase the plasma concentrations of atorvastatin, simvastatin, and their active acid metabolites.  The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of the statins and their metabolites by glecaprevir and pibrentasvir.  Inhibition of CYP450 3A4-mediated metabolism may also contribute, as glecaprevir and pibrentasvir are weak inhibitors of the isoenzyme.', NULL, 'Concomitant use of atorvastatin or simvastatin with glecaprevir-pibrentasvir is not recommended.', NULL, 'Concomitant use of atorvastatin or simvastatin with glecaprevir-pibrentasvir is not recommended.  Other statins may be used with glecaprevir-pibrentasvir, but a dosage reduction may be required.', 'Metabolism, Distribution', 'Amlodipine, Ramipril, Valsartan, Lisinopril, Perindopril, Niacin, Acetylsalicylic acid, Ezetimibe, Fenofibrate, Indapamide, Gemfibrozil, More', 'Tenofovir alafenamide, Cabotegravir, Maraviroc, Zanamivir, Rilpivirine, Dolutegravir, Brincidofovir, Elbasvir, Doravirine, Bictegravir, Pibrentasvir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299946/', '[1] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical  (2017):', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252574, 'Glecaprevir', 'Bempedoic acid', 'Moderate', 'Coadministration with bempedoic acid may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of the uptake transporters organic anion transporting polypeptide (OATP) 1B1 and 1B3.  The proposed mechanism, based on in vitro data, is decreased clearance due to bempedoic acid-mediated inhibition of OATP1B1 and 1B3.', NULL, 'Caution is advised if bempedoic acid is used concomitantly with drugs that are substrates of OATP1B1 or 1B3, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if bempedoic acid is used concomitantly with drugs that are substrates of OATP1B1 or 1B3, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever bempedoic acid is added to or withdrawn from therapy with these drugs.  Patients should be monitored for the development of adverse effects.', 'Absorption, Excretion', 'Gemfibrozil, Fenofibric acid, Mipomersen, Evinacumab, Evolocumab, Dextrothyroxine, Probucol, Cholestyramine, Ezetimibe, Clofibrate, Cerivastatin, More', 'Lamivudine, Tenofovir alafenamide, Maraviroc, Amprenavir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Rilpivirine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299947/', '[1] "Product Information. Nexlizet (bempedoic acid-ezetimibe)." Esperion Therapeutics  (2020):[2] "Product Information. Nexletol (bempedoic acid)." Esperion Therapeutics  (2020):', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252575, 'Glecaprevir', 'Berotralstat', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may significantly increase the plasma concentrations of berotralstat.  Berotralstat is a substrate of both P-gp and BCRP.', NULL, 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors.', NULL, 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors.  Patients should be advised to contact their physician if they experience undue adverse effects of berotralstat such as abdominal pain, vomiting, or diarrhea.  Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', 'Hemin, Voxelotor, Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Streptokinase, Human C1-esterase inhibitor, Crizanlizumab, Lanadelumab, Icatibant', 'Lamivudine, Cabotegravir, Simeprevir, Zanamivir, Valaciclovir, Telaprevir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299948/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Orladeyo (berotralstat)." BioCryst Pharmaceuticals Inc  (2021):', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252576, 'Glecaprevir', 'Betrixaban', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of betrixaban, which is a substrate of the efflux transporter.', NULL, 'Caution is advised when betrixaban is used with P-gp inhibitors.', NULL, 'Caution is advised when betrixaban is used with P-gp inhibitors.  Closer monitoring of the pharmacologic effects of betrixaban may be appropriate whenever a P-gp inhibitor is added to or withdrawn from therapy.  Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women).  Concomitant use of betrixaban with P-gp inhibitors should be avoided in patients with severe renal impairment (CrCl<30 mL/min).', 'Excretion', 'Ticagrelor, Clopidogrel, Cilostazol, Iloprost, Rivaroxaban, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Warfarin, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Valaciclovir, Zanamivir, Rilpivirine, Etravirine, Dolutegravir, Oseltamivir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299950/', '[1] "Product Information. Bevyxxa (betrixaban)." Portola Pharmaceuticals  (2017):', 1767369485);
